<script>
export default {
  name: 'IPila',
  props: {
    pila: {
      type: String,
      default: "",
    },
    ley_ag: {
      type: Number,
      default: 0,
    },
    stock: {
      type: Number,
      default: 0,
    },
    mining1 : {
      type: String,
      default: "",
    },
    mining2 : {
      type: String,
      default: "",
    },
    dominio1 : {
      type: String,
      default: "",
    },
    dominio2 : {
      type: String,
      default: "",
    },
    isMerged: {
      type: Boolean,
      default: false,
    },
    statusPila: {
      type: String,
      default: "",
    },
    createdAt: {
      type: String,
      default: "",
    },
  },
  methods: {
    formatNumber: (val) => {
        if (!val) return '-'
        return `${val.toFixed(1).replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`
    },
    chooseNumberStar: (val) => {
      if (val == "Analizando") {return 1}
      if (val == "waitBeginAbastecimiento") {return 2}
      if (val == "waitBeginDespacho") {return 3}
    },
    isNow: (val) => {
      // check if createdAt is today
      const today = new Date()
      const createdAt = new Date(val)
      return today.toDateString() === createdAt.toDateString()
    }
  }
};
</script>